<script>
import JoeyLib from "./lib/JoeyLib"

export default {
  data: () => ({
    instance: null,
    scrollSpeed: 15
  }),


  mounted(){
    // Create Instance
    this.instance = new JoeyLib('webcam',this.fnGoUp, this.fnGoDown)
    this.instance.init()
  },

  methods:{
    fnGoUp(){
      window.scrollBy(0, -this.scrollSpeed);
    },
    fnGoDown(){
      window.scrollBy(0, this.scrollSpeed);
    }
  }

  
}
</script>

